jQuery(document).ready(function() {
    jQuery("#selected_language a").click(function (e) {
        e.preventDefault();
        jQuery("#locale_list").show().position({
            of: $("#selected_language"),
            my: "left top",
            at: "left bottom",
            offset: "0 3",
            collision: "fit fit"
        });
        jQuery(document).click(function () {
            jQuery("#locale_list").fadeOut("fast");
        });
        return false;
    });
});

